    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened


        this.setDefaultCloseOperation(this.DISPOSE_ON_CLOSE);
      Helper.center(this);
      this.pf_confirm_pass.setEnabled(false);
      this.pf_new_pass.setEnabled(false);
      this.btn_change.setEnabled(false);
      
    }//GEN-LAST:event_formWindowOpened

    private void pf_current_passKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_pf_current_passKeyReleased

        if(evt.getKeyCode() ==10){
         DBConnect d = new DBConnect();
         String pass  = d.covertPassword(pf_current_pass.getPassword());
            String query  =  "SELECT * FROM users WHERE id='"+currentUserId+"' AND password = PASSWORD('"+pass+"')";

            try{
                d.rs = d.st.executeQuery(query);
                d.rs.next();
                d.rs.getString("password");

                if(!d.rs.getString("password").isEmpty()){
                    pf_new_pass.setEnabled(true);
                    pf_confirm_pass.setEnabled(true);
                    pf_current_pass.setEnabled(false);
                     this.btn_change.setEnabled(true);
                }else{
                    Helper.showNotification("Worng password!");
                }

            }catch (Exception e){
                Helper.showNotification("Worng password!");
            }
        };
    }//GEN-LAST:event_pf_current_passKeyReleased

