    private void btn_changeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_changeActionPerformed

           DBConnect d = new DBConnect();
         String newPass  = d.covertPassword(pf_new_pass.getPassword());
          String confirmPass  = d.covertPassword(pf_confirm_pass.getPassword());
       if(newPass.equals(confirmPass)){
           
           String queryUpdate = "UPDATE users SET password = PASSWORD('"+newPass+"') WHERE id ="+currentUserId;
         boolean passChange =   d.excute(queryUpdate);
         if(passChange==true){
             Helper.showNotification("Password change successflly!");
             this.setVisible(false);
         }else{
             Helper.showNotification("Qurey faild!");
         }
           
       }else{
           Helper.showNotification("Password does not match!");
       }
    }//GEN-LAST:event_btn_changeActionPerformed

